const display=document.getElementById("display"),numButtons=document.querySelectorAll("#nums .numButton");let expression="",previousResult=null;numButtons.forEach((e=>{e.addEventListener("click",(()=>{expression+=e.textContent,display.textContent=expression}))}));const equalsButton=document.getElementById("equalsButton");equalsButton.addEventListener("click",(()=>{if(expression.length>0){const result=eval(expression);display.textContent=result,expression=result.toString()}}));const deleteButton=document.getElementById("deleteButton");deleteButton.addEventListener("click",(()=>{expression=expression.slice(0,-1),display.textContent=expression}));const clearButton=document.getElementById("clearButton");clearButton.addEventListener("click",(()=>{expression="",display.textContent=""}));